<?php$route = '/contacts/:contact_id/';$app->delete($route, function ($contact_id)  use ($app){	$request = $app->request();	$_GET = $request->params();	$_BODY = $request->getBody();	$_BODY = json_decode($_BODY,true);				$contact_id = filter_var($contact_id, FILTER_SANITIZE_STRING);	$Query = "DELETE FROM contact WHERE id = '" . $contact_id . "'";	mysql_query($Query) or die('Query failed: ' . mysql_error());	$ReturnObject = array();	$ReturnObject['message'] = "Contact Deleted";	$ReturnObject['contact_id'] = $contact_id;	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>