<?php$route = '/contacts/:contact_id/';$app->get($route, function ($contact_id)  use ($app){	$ReturnObject = array();	$contact_id = mysql_real_escape_string($contact_id);	$Query = "SELECT * FROM contact WHERE id = '" . $contact_id . "'";	//echo $Query;	$DatabaseResult = mysql_query($Query) or die('Query failed: ' . mysql_error());	while ($Database = mysql_fetch_assoc($DatabaseResult))		{		$id = $Database['id'];		$organization_id = $Database['organization_id'];		$service_id = $Database['service_id'];		$service_at_location_id = $Database['service_at_location_id'];		$name = $Database['name'];		$title = $Database['title'];		$department = $Database['department'];		$email = $Database['email'];		$F = array();		$F['id'] = $id;		$F['organization_id'] = $organization_id;		$F['service_id'] = $service_id;		$F['service_at_location_id'] = $service_at_location_id;		$F['name'] = $name;		$F['title'] = $title;		$F['department'] = $department;		$F['email'] = $email;			$ReturnObject = $F;		}	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>