<?php$route = '/contacts/:contact_id/phones/:phone_id/';$app->delete($route, function ($contact_id,$phone_id)  use ($app){	$contact_id = filter_var($contact_id, FILTER_SANITIZE_STRING);	$phone_id = filter_var($phone_id, FILTER_SANITIZE_STRING);	$Query = "DELETE FROM phone WHERE id = '" . $phone_id . "' AND contact_id = '" . $contact_id . "'";	mysql_query($Query) or die('Query failed: ' . mysql_error());	$ReturnObject = array();	$ReturnObject['message'] = "Phone Deleted";	$ReturnObject['id'] = $phone_id;	$ReturnObject['contact_id'] = $contact_id;	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>