<?php$route = '/contacts/:contact_id/phones/';$app->post($route, function ($contact_id)  use ($app){	$request = $app->request();	$_GET = $request->params();	$_BODY = $request->getBody();	$_BODY = json_decode($_BODY,true);		$clean = filter_var_array($_BODY,FILTER_SANITIZE_STRING);			$contact_id = filter_var($contact_id, FILTER_SANITIZE_STRING);		if(isset($clean['number']))		{		$phone_id = getGUID();				$location_id = $clean['location_id'];		$service_id = $clean['service_id'];		$organization_id = $clean['organization_id'];		$service_at_location_id = $clean['service_at_location_id'];						$number = $clean['number'];		$extension = $clean['extension'];		$type = $clean['type'];		$department = $clean['department'];				$language = $clean['language'];		$description = $clean['description'];		$query = "INSERT INTO phone(";				$query .= "id,";		$query .= "location_id,";		$query .= "service_id,";		$query .= "organization_id,";		$query .= "contact_id,";		$query .= "service_at_location_id,";		$query .= "number,";		$query .= "extension,";		$query .= "type,";		$query .= "department,";		$query .= "language,";		$query .= "description";		$query .= ") VALUES(";		$query .= "'" . mysql_real_escape_string($phone_id) . "',";		$query .= "'" . mysql_real_escape_string($location_id) . "',";		$query .= "'" . mysql_real_escape_string($service_id) . "',";		$query .= "'" . mysql_real_escape_string($organization_id) . "',";		$query .= "'" . mysql_real_escape_string($contact_id) . "',";		$query .= "'" . mysql_real_escape_string($service_at_location_id) . "',";		$query .= "'" . mysql_real_escape_string($number) . "',";		$query .= "'" . mysql_real_escape_string($extension) . "',";		$query .= "'" . mysql_real_escape_string($type) . "',";		$query .= "'" . mysql_real_escape_string($department) . "',";		$query .= "'" . mysql_real_escape_string($language) . "',";		$query .= "'" . mysql_real_escape_string($description) . "'";		$query .= ")";		mysql_query($query) or die('Query failed: ' . mysql_error());		$F = array();		$F['id'] = $phone_id;		$F['location_id'] = $location_id;				$F['service_id'] = $service_id;		$F['organization_id'] = $organization_id;		$F['contact_id'] = $contact_id;		$F['service_at_location_id'] = $service_at_location_id;		$F['number'] = $number;		$F['extension'] = $extension;		$F['type'] = $type;		$F['department'] = $department;		$F['language'] = $language;		$F['description'] = $description;		$ReturnObject = $F;		}			$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>