<?php$route = '/locations/:location_id/accessibility/:accessibility_id/';$app->delete($route, function ($location_id,$accessibility_id)  use ($app){	$location_id = mysql_real_escape_string($location_id);	$accessibility_id = mysql_real_escape_string($accessibility_id);		$Query = "DELETE FROM accessibility_for_disabilities WHERE id = '" . $accessibility_id . "' AND location_id = '" . $location_id . "'";	mysql_query($Query) or die('Query failed: ' . mysql_error());	$ReturnObject = array();	$ReturnObject['message'] = "Accessibility Deleted";	$ReturnObject['id'] = $accessibility_id;	$ReturnObject['location_id'] = $location_id;	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>