<?php$route = '/locations/:location_id/accessibility/:accessibility_id/';$app->put($route, function ($location_id,$accessibility_id)  use ($app){	$request = $app->request();	$_BODY = $request->getBody();	$_BODY = json_decode($_BODY,true);			$location_id = filter_var($location_id, FILTER_SANITIZE_STRING);	$accessibility_id = filter_var($accessibility_id, FILTER_SANITIZE_STRING);	$accessibility = filter_var($_BODY['accessibility'], FILTER_SANITIZE_STRING);	$details = filter_var($_BODY['details'], FILTER_SANITIZE_STRING);	$query = "UPDATE accessibility_for_disabilities SET ";	$query .= "location_id='" . mysql_real_escape_string($location_id) . "'";	$query .= ",id='" . mysql_real_escape_string($accessibility_id) . "'";	$query .= ",accessibility='" . mysql_real_escape_string($accessibility) . "'";	$query .= ",details='" . mysql_real_escape_string($details) . "'";	$query .= " WHERE id = '" . $accessibility_id . "'";	//echo $query;	mysql_query($query) or die('Query failed: ' . mysql_error());	$F = array();	$F['id'] = $accessibility_id;	$F['location_id'] = $location_id;	$F['accessibility'] = $accessibility;	$F['details'] = $details;		$ReturnObject = $F;	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>