<?php$route = '/locations/:location_id/accessibility/';$app->post($route, function ($location_id)  use ($app){	$request = $app->request();	$_GET = $request->params();	$_BODY = $request->getBody();	$_BODY = json_decode($_BODY,true);		$clean = filter_var_array($_BODY,FILTER_SANITIZE_STRING);		$location_id = filter_var($location_id, FILTER_SANITIZE_STRING);		if(isset($clean['accessibility']))		{		$accessibility_id = getGUID();				$accessibility = $clean['accessibility'];		$details = $clean['details'];		$query = "INSERT INTO accessibility_for_disabilities(";				$query .= "location_id,";		$query .= "id,";		$query .= "accessibility,";		$query .= "details";		$query .= ") VALUES(";		$query .= "'" . mysql_real_escape_string($location_id) . "',";		$query .= "'" . mysql_real_escape_string($accessibility_id) . "',";		$query .= "'" . mysql_real_escape_string($accessibility_id) . "',";		$query .= "'" . mysql_real_escape_string($details) . "'";		$query .= ")";		mysql_query($query) or die('Query failed: ' . mysql_error());		$F = array();		$F['id'] = $accessibility_id;		$F['location_id'] = $location_id;		$F['accessibility'] = $accessibility;		$F['details'] = $details;		$ReturnObject = $F;		}			$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>