<?php$route = '/locations/:location_id/';$app->delete($route, function ($location_id)  use ($app){	$location_id = filter_var($location_id, FILTER_SANITIZE_STRING);	$Query = "DELETE FROM location WHERE id = '" . $location_id . "'";	mysql_query($Query) or die('Query failed: ' . mysql_error());	$ReturnObject = array();	$ReturnObject['message'] = "Location Deleted";	$ReturnObject['location_id'] = $location_id;	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>