<?php$route = '/locations/:location_id/';$app->get($route, function ($location_id)  use ($app){	$request = $app->request();	$_GET = $request->params();		$location_id = filter_var($location_id, FILTER_SANITIZE_STRING);	$ReturnObject = array();	$Query = "SELECT * FROM location WHERE id = '" . $location_id . "'";	$DatabaseResult = mysql_query($Query) or die('Query failed: ' . mysql_error());	while ($Database = mysql_fetch_assoc($DatabaseResult))		{		$id = $Database['id'];		$organization_id = $Database['organization_id'];		$name = $Database['name'];		$alternate_name = $Database['alternate_name'];		$description = $Database['description'];		$transportation = $Database['transportation'];		$latitude = $Database['latitude'];		$longitude = $Database['longitude'];		$F = array();		$F['id'] = $id;		$F['organization_id'] = $organization_id;		$F['name'] = $name;		$F['alternate_name'] = $alternate_name;		$F['description'] = $description;		$F['transportation'] = $transportation;		$F['latitude'] = $latitude;		$F['longitude'] = $longitude;		$ReturnObject = $F;		}	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>