<?php$route = '/locations/:location_id/holiday-schedule/:holiday_schedule_id/';$app->delete($route, function ($location_id,$holiday_schedule_id)  use ($app){	$location_id = filter_var($location_id, FILTER_SANITIZE_STRING);	$holiday_schedule_id = filter_var($holiday_schedule_id, FILTER_SANITIZE_STRING);	$Query = "DELETE FROM holiday_schedule WHERE id = '" . $holiday_schedule_id . "' AND location_id = '" . $location_id . "'";	mysql_query($Query) or die('Query failed: ' . mysql_error());	$ReturnObject = array();	$ReturnObject['message'] = "Holiday_schedule Deleted!";	$ReturnObject['id'] = $holiday_schedule_id;	$ReturnObject['location_id'] = $location_id;	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>