<?php$route = '/locations/:location_id/holiday-schedule/:holiday_schedule_id/';$app->put($route, function ($location_id,$holiday_schedule_id)  use ($app){	$request = $app->request();	$_GET = $request->params();	$_BODY = $request->getBody();	$_BODY = json_decode($_BODY,true);				$location_id = filter_var($location_id, FILTER_SANITIZE_STRING);	$holiday_schedule_id = filter_var($holiday_schedule_id, FILTER_SANITIZE_STRING);		$service_id = filter_var($_BODY['service_id'], FILTER_SANITIZE_STRING);	$service_at_location_id = filter_var($_BODY['service_at_location_id'], FILTER_SANITIZE_STRING);	$closed = filter_var($_BODY['closed'], FILTER_SANITIZE_STRING);	$opens_at = filter_var($_BODY['opens_at'], FILTER_SANITIZE_STRING);	$closes_at = filter_var($_BODY['closes_at'], FILTER_SANITIZE_STRING);	$start_date = filter_var($_BODY['start_date'], FILTER_SANITIZE_STRING);	$end_date = filter_var($_BODY['end_date'], FILTER_SANITIZE_STRING);	$query = "UPDATE holiday_schedule SET ";	$query .= "service_id='" . mysql_real_escape_string($service_id) . "'";	$query .= ",location_id='" . mysql_real_escape_string($location_id) . "'";	$query .= ",service_at_location_id='" . mysql_real_escape_string($service_at_location_id) . "'";	$query .= ",closed='" . mysql_real_escape_string($closed) . "'";	$query .= ",opens_at='" . mysql_real_escape_string($opens_at) . "'";	$query .= ",closes_at='" . mysql_real_escape_string($closes_at) . "'";	$query .= ",start_date='" . mysql_real_escape_string($start_date) . "'";	$query .= ",end_date='" . mysql_real_escape_string($end_date) . "'";	$query .= " WHERE id = '" . $holiday_schedule_id . "'";	//echo $query;	mysql_query($query) or die('Query failed: ' . mysql_error());	$F = array();	$F['id'] = $holiday_schedule_id;	$F['service_id'] = $service_id;	$F['location_id'] = $location_id;	$F['service_at_location_id'] = $service_at_location_id;	$F['closed'] = $closed;	$F['opens_at'] = $opens_at;	$F['closes_at'] = $closes_at;	$F['start_date'] = $start_date;	$F['end_date'] = $end_date;		$ReturnObject = $F;	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>