<?php$route = '/locations/:location_id/holiday-schedule/';$app->post($route, function ($location_id)  use ($app){	$request = $app->request();	$_GET = $request->params();	$_BODY = $request->getBody();	$_BODY = json_decode($_BODY,true);		$clean = filter_var_array($_BODY,FILTER_SANITIZE_STRING);		$location_id = filter_var($location_id, FILTER_SANITIZE_STRING);		if(isset($clean['closed']))		{		$holiday_schedule_id = getGUID();		$service_id = $clean['service_id'];		$service_at_location_id = $clean['service_at_location_id'];		$closed = $clean['closed'];		$opens_at = $clean['opens_at'];		$closes_at = $clean['closes_at'];		$start_date = $clean['start_date'];		$end_date = $clean['end_date'];		$query = "INSERT INTO holiday_schedule(";				$query .= "id,";		$query .= "service_id,";		$query .= "location_id,";		$query .= "service_at_location_id,";		$query .= "closed,";		$query .= "opens_at,";		$query .= "closes_at,";		$query .= "start_date,";		$query .= "end_date";		$query .= ") VALUES(";		$query .= "'" . mysql_real_escape_string($holiday_schedule_id) . "',";		$query .= "'" . mysql_real_escape_string($service_id) . "',";		$query .= "'" . mysql_real_escape_string($location_id) . "',";		$query .= "'" . mysql_real_escape_string($service_at_location_id) . "',";		$query .= "'" . mysql_real_escape_string($closed) . "',";		$query .= "'" . mysql_real_escape_string($opens_at) . "',";		$query .= "'" . mysql_real_escape_string($closes_at) . "',";		$query .= "'" . mysql_real_escape_string($start_date) . "',";		$query .= "'" . mysql_real_escape_string($end_date) . "'";		$query .= ")";		mysql_query($query) or die('Query failed: ' . mysql_error());		$F = array();		$F['id'] = $holiday_schedule_id;		$F['service_id'] = $service_id;		$F['location_id'] = $location_id;		$F['service_at_location_id'] = $service_at_location_id;		$F['closed'] = $closed;		$F['opens_at'] = $opens_at;		$F['closes_at'] = $closes_at;		$F['start_date'] = $start_date;		$F['end_date'] = $end_date;		$ReturnObject = $F;		}			$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>