<?php$route = '/locations/:location_id/languages/';$app->get($route, function ($location_id)  use ($app){	$request = $app->request();	$_GET = $request->params();		$location_id = filter_var($location_id, FILTER_SANITIZE_STRING);	$ReturnObject = array();	$Query = "SELECT * FROM language WHERE location_id = '" . $location_id . "'";	$Query .= " ORDER BY language ASC";	//echo $Query;	$DatabaseResult = mysql_query($Query) or die('Query failed: ' . mysql_error());	while ($Database = mysql_fetch_assoc($DatabaseResult))		{		$language_id = $Database['id'];		$language = $Database['language'];		$F = array();		$F['id'] = $language_id;		$F['location_id'] = $location_id;		$F['language'] = $language;				array_push($ReturnObject,$F);				}	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));		});?>