<?php$route = '/locations/:location_id/languages/:language_id/';$app->delete($route, function ($location_id,$language_id)  use ($app){	$location_id = filter_var($location_id, FILTER_SANITIZE_STRING);	$language_id = filter_var($language_id, FILTER_SANITIZE_STRING);	$Query = "DELETE FROM language WHERE id = '" . $language_id . "' AND location_id = '" . $location_id . "'";	mysql_query($Query) or die('Query failed: ' . mysql_error());	$ReturnObject = array();	$ReturnObject['id'] = $language_id;	$ReturnObject['message'] = "Language Deleted";	$ReturnObject['location_id'] = $location_id;	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>