<?php$route = '/locations/:location_id/languages/:language_id/';$app->put($route, function ($location_id,$language_id)  use ($app){	$request = $app->request();	$_GET = $request->params();	$_BODY = $request->getBody();	$_BODY = json_decode($_BODY,true);				$location_id = filter_var($location_id, FILTER_SANITIZE_STRING);	$language_id = filter_var($language_id, FILTER_SANITIZE_STRING);	$language = filter_var($_BODY['language'], FILTER_SANITIZE_STRING);	$query = "UPDATE language SET ";	$query .= "location_id='" . mysql_real_escape_string($location_id) . "'";	$query .= ",language='" . mysql_real_escape_string($language) . "'";	$query .= " WHERE id = '" . $language_id . "'";	//echo $query;	mysql_query($query) or die('Query failed: ' . mysql_error());	$F = array();	$F['id'] = $language_id;	$F['location_id'] = $location_id;	$F['language'] = $language;		$ReturnObject = $F;	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>