<?php$route = '/locations/:location_id/languages/';$app->post($route, function ($location_id)  use ($app){	$request = $app->request();	$_GET = $request->params();	$_BODY = $request->getBody();	$_BODY = json_decode($_BODY,true);		$clean = filter_var_array($_BODY,FILTER_SANITIZE_STRING);		$location_id = filter_var($location_id, FILTER_SANITIZE_STRING);		if(isset($clean['language']))		{		$language_id = getGUID();				$language = $clean['language'];		$query = "INSERT INTO language(";				$query .= "location_id,";		$query .= "id,";		$query .= "language";		$query .= ") VALUES(";		$query .= "'" . mysql_real_escape_string($location_id) . "',";		$query .= "'" . mysql_real_escape_string($language_id) . "',";		$query .= "'" . mysql_real_escape_string($language) . "'";		$query .= ")";		mysql_query($query) or die('Query failed: ' . mysql_error());		$F = array();		$F['id'] = $language_id;		$F['location_id'] = $location_id;		$F['language'] = $language;		$ReturnObject = $F;		}			$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>