<?php$route = '/locations/:location_id/phones/:phone_id/';$app->delete($route, function ($location_id,$phone_id)  use ($app){	$location_id = filter_var($location_id, FILTER_SANITIZE_STRING);	$phone_id = filter_var($phone_id, FILTER_SANITIZE_STRING);	$Query = "DELETE FROM phone WHERE id = '" . $phone_id . "' AND location_id = '" . $location_id . "'";	mysql_query($Query) or die('Query failed: ' . mysql_error());	$ReturnObject = array();	$ReturnObject['message'] = "Phone Deleted!";	$ReturnObject['id'] = $phone_id;	$ReturnObject['location_id'] = $location_id;	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>