<?php$route = '/locations/:location_id/physical-address/:postal_address_id/';$app->delete($route, function ($location_id,$physical_address_id)  use ($app){	$location_id = filter_var($location_id, FILTER_SANITIZE_STRING);	$physical_address_id = filter_var($physical_address_id, FILTER_SANITIZE_STRING);	$Query = "DELETE FROM postal_address WHERE id = '" . $physical_address_id . "' AND location_id = '" . $location_id . "'";	mysql_query($Query) or die('Query failed: ' . mysql_error());	$ReturnObject = array();	$ReturnObject['message'] = "Postal_address Deleted";	$ReturnObject['id'] = $physical_address_id;	$ReturnObject['location_id'] = $location_id;	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>