<?php$route = '/locations/:location_id/physical-address/:physical_address_id/';$app->put($route, function ($location_id,$physical_address_id)  use ($app){	$request = $app->request();	$_BODY = $request->getBody();	$_BODY = json_decode($_BODY,true);		$location_id = filter_var($location_id, FILTER_SANITIZE_STRING);	$physical_address_id = filter_var($physical_address_id, FILTER_SANITIZE_STRING);			$attention = filter_var($_BODY['attention'], FILTER_SANITIZE_STRING);	$address_1 = filter_var($_BODY['address_1'], FILTER_SANITIZE_STRING);	$address_2 = filter_var($_BODY['address_2'], FILTER_SANITIZE_STRING);	$address_3 = filter_var($_BODY['address_3'], FILTER_SANITIZE_STRING);	$address_4 = filter_var($_BODY['address_4'], FILTER_SANITIZE_STRING);	$city = filter_var($_BODY['city'], FILTER_SANITIZE_STRING);	$state_province = filter_var($_BODY['state_province'], FILTER_SANITIZE_STRING);	$postal_code = filter_var($_BODY['postal_code'], FILTER_SANITIZE_STRING);	$country = filter_var($_BODY['country'], FILTER_SANITIZE_STRING);	$query = "UPDATE physical_address SET ";	$query .= "location_id='" . mysql_real_escape_string($location_id) . "'";	$query .= ",attention='" . mysql_real_escape_string($attention) . "'";	$query .= ",address_1='" . mysql_real_escape_string($address_1) . "'";	$query .= ",address_2='" . mysql_real_escape_string($address_2) . "'";	$query .= ",address_3='" . mysql_real_escape_string($address_3) . "'";	$query .= ",address_4='" . mysql_real_escape_string($address_4) . "'";	$query .= ",city='" . mysql_real_escape_string($city) . "'";	$query .= ",state_province='" . mysql_real_escape_string($state_province) . "'";	$query .= ",postal_code='" . mysql_real_escape_string($postal_code) . "'";	$query .= ",country='" . mysql_real_escape_string($country) . "'";	$query .= " WHERE id = '" . $physical_address_id . "'";	//echo $query;	mysql_query($query) or die('Query failed: ' . mysql_error());	$F = array();	$F['id'] = $physical_address_id;	$F['location_id'] = $location_id;		$F['attention'] = $attention;	$F['address_1'] = $address_1;	$F['address_2'] = $address_2;	$F['address_3'] = $address_3;	$F['address_4'] = $address_4;	$F['city'] = $city;	$F['state_province'] = $state_province;	$F['postal_code'] = $postal_code;	$F['country'] = $country;	$ReturnObject = $F;	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>