<?php$route = '/locations/:location_id/regular-schedule/';$app->post($route, function ($location_id)  use ($app){	$request = $app->request();	$_GET = $request->params();	$_BODY = $request->getBody();	$_BODY = json_decode($_BODY,true);		$clean = filter_var_array($_BODY,FILTER_SANITIZE_STRING);		$location_id = filter_var($location_id, FILTER_SANITIZE_STRING);		if(isset($clean['weekday']))		{		$regular_schedule_id = getGUID();		$service_id = $clean['service_id'];		$service_at_location_id = $clean['service_at_location_id'];				$weekday = $clean['weekday'];		$opens_at = $clean['opens_at'];		$closes_at = $clean['closes_at'];		$query = "INSERT INTO regular_schedule(";				$query .= "id,";		$query .= "location_id,";		$query .= "service_id,";		$query .= "service_at_location_id,";		$query .= "weekday,";		$query .= "opens_at,";		$query .= "closes_at";		$query .= ") VALUES(";		$query .= "'" . mysql_real_escape_string($regular_schedule_id) . "',";		$query .= "'" . mysql_real_escape_string($location_id) . "',";		$query .= "'" . mysql_real_escape_string($service_id) . "',";		$query .= "'" . mysql_real_escape_string($service_at_location_id) . "',";		$query .= "'" . mysql_real_escape_string($weekday) . "',";		$query .= "'" . mysql_real_escape_string($opens_at) . "',";		$query .= "'" . mysql_real_escape_string($closes_at) . "'";		$query .= ")";		mysql_query($query) or die('Query failed: ' . mysql_error());		$F = array();		$F['id'] = $regular_schedule_id;		$F['location_id'] = $location_id;		$F['service_id'] = $service_id;		$F['service_at_location_id'] = $service_at_location_id;		$F['weekday'] = $weekday;		$F['opens_at'] = $opens_at;		$F['closes_at'] = $closes_at;		$ReturnObject = $F;		}			$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>