<?php$route = '/locations/:location_id/regular-schedule/:regular_schedule_id/';$app->delete($route, function ($location_id,$regular_schedule_id)  use ($app){	$location_id = filter_var($location_id, FILTER_SANITIZE_STRING);	$regular_schedule_id = filter_var($regular_schedule_id, FILTER_SANITIZE_STRING);	$Query = "DELETE FROM regular_schedule WHERE id = '" . $regular_schedule_id . "' AND location_id = '" . $location_id . "'";	mysql_query($Query) or die('Query failed: ' . mysql_error());	$ReturnObject = array();	$ReturnObject['message'] = "Regular_schedule Deleted!";	$ReturnObject['id'] = $location_id;	$ReturnObject['regular_schedule_id'] = $regular_schedule_id;	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>