<?php$route = '/locations/:location_id/services/:service_id/';$app->get($route, function ($location_id,$service_id)  use ($app){	$ReturnObject = array();	$service_id = mysql_real_escape_string($service_id);	$location_id = mysql_real_escape_string($location_id);	$Query = "SELECT * FROM service s JOIN service_at_location sal ON s.service_id = sal.service_id  WHERE sal.service_id = '" . $service_id . "' and sal.location_id = '" . $location_id . "'";	///echo $Query;	$DatabaseResult = mysql_query($Query) or die('Query failed: ' . mysql_error());	while ($Database = mysql_fetch_assoc($DatabaseResult))		{		$id = $Database['id'];		$organization_id = $Database['organization_id'];		$program_id = $Database['program_id'];		$name = $Database['name'];		$alternate_name = $Database['alternate_name'];		$url = $Database['url'];		$email = $Database['email'];		$status = $Database['status'];		$application_process = $Database['application_process'];		$wait_time = $Database['wait_time'];		$F = array();		$F['id'] = $id;		$F['organization_id'] = $organization_id;		$F['program_id'] = $program_id;		$F['name'] = $name;		$F['alternate_name'] = $alternate_name;		$F['url'] = $url;		$F['email'] = $email;		$F['status'] = $status;		$F['application_process'] = $application_process;		$F['wait_time'] = $wait_time;		$ReturnObject = $F;		}	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>