<?php$route = '/contacts/:contact_id/phones';$app->get($route, function ()  use ($app){	$request = $app->request();	$_GET = $request->params();	$ReturnObject = array();	if($query=='')		{		$Query = "SELECT * FROM phone WHERE name LIKE '%" . $query . "%'";		}	else		{		$Query = "SELECT * FROM phone";		}	$Query .= " ORDER BY name ASC";	$DatabaseResult = mysql_query($Query) or die('Query failed: ' . mysql_error());	while ($Database = mysql_fetch_assoc($DatabaseResult))		{		$id = $Database['id'];		$location_id = $Database['location_id'];		$service_id = $Database['service_id'];		$organization_id = $Database['organization_id'];		$contact_id = $Database['contact_id'];		$number = $Database['number'];		$extension = $Database['extension'];		$type = $Database['type'];		$department = $Database['department'];		$F = array();		$F['id'] = $id;		$F['location_id'] = $location_id;		$F['service_id'] = $service_id;		$F['organization_id'] = $organization_id;		$F['contact_id'] = $contact_id;		$F['number'] = $number;		$F['extension'] = $extension;		$F['type'] = $type;		$F['department'] = $department;		array_push($ReturnObject, $F);		}	$api->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>