<?php$route = '/contacts/';$app->post($route, function ()  use ($app){ 	$ReturnObject = array(); 	$request = $app->request();	$_GET = $request->params();	$clean = filter_input_array(INPUT_POST,$_GET);	if(isset($clean['name']))		{		$id = getGUID();				$organization_id = $clean['organization_id'];		$service_id = $clean['service_id'];		$name = $clean['name'];		$title = $clean['title'];		$department = $clean['department'];		$email = $clean['email'];		$query = "INSERT INTO contact(";				$query .= "id,";		$query .= "organization_id,";		$query .= "service_id,";		$query .= "name,";		$query .= "title,";		$query .= "department,";		$query .= "email";		$query .= ") VALUES(";		$query .= "'" . mysql_real_escape_string($id) . "',";		$query .= "'" . mysql_real_escape_string($organization_id) . "',";		$query .= "'" . mysql_real_escape_string($service_id) . "',";		$query .= "'" . mysql_real_escape_string($name) . "',";		$query .= "'" . mysql_real_escape_string($title) . "',";		$query .= "'" . mysql_real_escape_string($department) . "',";		$query .= "'" . mysql_real_escape_string($email) . "'";		$query .= ")";		mysql_query($query) or die('Query failed: ' . mysql_error());		$F = array();		$F['id'] = $id;		$F['organization_id'] = $organization_id;		$F['service_id'] = $service_id;		$F['name'] = $name;		$F['title'] = $title;		$F['department'] = $department;		$F['email'] = $email;		$ReturnObject = $F;		}	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>