<?php$route = '/locations/';$app->get($route, function ()  use ($app){	$request = $app->request();	$_GET = $request->params();	if(isset($_GET['query'])){ $query = $_GET['query']; } else { $query = '';}	$ReturnObject = array();	if($query=='')		{		$Query = "SELECT * FROM location WHERE name LIKE '%" . $query . "%'";		}	else		{		$Query = "SELECT * FROM location";		}	$Query .= " ORDER BY name ASC";	$DatabaseResult = mysql_query($Query) or die('Query failed: ' . mysql_error());	while ($Database = mysql_fetch_assoc($DatabaseResult))		{		$id = $Database['id'];		$organization_id = $Database['organization_id'];		$name = $Database['name'];		$alternate_name = $Database['alternate_name'];		$transportation = $Database['transportation'];		$latitude = $Database['latitude'];		$longitude = $Database['longitude'];		$F = array();		$F['id'] = $id;		$F['organization_id'] = $organization_id;		$F['name'] = $name;		$F['alternate_name'] = $alternate_name;		$F['transportation'] = $transportation;		$F['latitude'] = $latitude;		$F['longitude'] = $longitude;		array_push($ReturnObject, $F);		}	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>