<?php$route = '/locations/:location_id/accessibility/:accessibility_id/';$app->get($route, function ($location_id,$accessibility_id)  use ($app){	$ReturnObject = array();	$location_id = mysql_real_escape_string($location_id);	$accessibility_id = mysql_real_escape_string($accessibility_id);	$Query = "SELECT * FROM accessibility_for_disabilities WHERE id = '" . $accessibility_id . "' AND location_id = '" . $location_id . "'";	//echo $Query;	$DatabaseResult = mysql_query($Query) or die('Query failed: ' . mysql_error());	while ($Database = mysql_fetch_assoc($DatabaseResult))		{		$accessibility = $Database['accessibility'];		$F = array();		$F['location_id'] = $location_id;		$F['id'] = $accessibility_id;		$F['accessibility'] = $accessibility;				$ReturnObject = $F;		}	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>