<?php$route = '/locations/:location_id/accessibility/:accessibility_id/';$app->put($route, function ($location_id,$accessibility_id)  use ($app){	$request = $app->request();	$_GET = $request->params();		$location_id = mysql_real_escape_string($location_id);	$accessibility_id = mysql_real_escape_string($accessibility_id);	$accessibility = mysql_real_escape_string($_GET['accessibility']);	$query = "UPDATE accessibility_for_disabilities SET ";	$query .= "location_id='" . mysql_real_escape_string($location_id) . "'";	$query .= ",id='" . mysql_real_escape_string($accessibility_id) . "'";	$query .= ",accessibility='" . mysql_real_escape_string($accessibility) . "'";	$query .= " WHERE id = '" . $accessibility_id . "'";	//echo $query;	mysql_query($query) or die('Query failed: ' . mysql_error());	$F = array();	$F['location_id'] = $location_id;	$F['id'] = $accessibility_id;	$F['accessibility'] = $accessibility;		$ReturnObject = $F;	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>