<?php$route = '/locations/:location_id/accessibility/';$app->post($route, function ($location_id)  use ($app){	$request = $app->request();	$_GET = $request->params();		$clean = filter_input_array(INPUT_POST,$_GET);		$location_id = mysql_real_escape_string($location_id);		if(isset($clean['accessibility']))		{		$accessibility_id = getGUID();				$accessibility = $clean['accessibility'];		$query = "INSERT INTO accessibility_for_disabilities(";				$query .= "location_id,";		$query .= "id,";		$query .= "accessibility";		$query .= ") VALUES(";		$query .= "'" . mysql_real_escape_string($location_id) . "',";		$query .= "'" . mysql_real_escape_string($accessibility_id) . "',";		$query .= "'" . mysql_real_escape_string($accessibility) . "'";		$query .= ")";		mysql_query($query) or die('Query failed: ' . mysql_error());		$F = array();		$F['location_id'] = $location_id;		$F['id'] = $accessibility_id;		$F['accessibility'] = $accessibility;		$ReturnObject = $F;		}			$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>