<?php$route = '/locations/:location_id/';$app->get($route, function ($location_id)  use ($app){	$request = $app->request();	$_GET = $request->params();		$id = filter_var($location_id, FILTER_SANITIZE_STRING);	$ReturnObject = array();	$Query = "SELECT * FROM location WHERE id = '" . $id . "'";	$DatabaseResult = mysql_query($Query) or die('Query failed: ' . mysql_error());	while ($Database = mysql_fetch_assoc($DatabaseResult))		{		$organization_id = $Database['organization_id'];		$name = $Database['name'];		$alternate_name = $Database['alternate_name'];		$transportation = $Database['transportation'];		$latitude = $Database['latitude'];		$longitude = $Database['longitude'];		$F = array();		$F['id'] = $id;		$F['organization_id'] = $organization_id;		$F['name'] = $name;		$F['alternate_name'] = $alternate_name;		$F['transportation'] = $transportation;		$F['latitude'] = $latitude;		$F['longitude'] = $longitude;		$F['services'] = array();				$Query = "SELECT * FROM service WHERE location_id = '" . $location_id . "'";		$Query .= " ORDER BY name ASC";		echo $Query;		$DatabaseResult = mysql_query($Query) or die('Query failed: ' . mysql_error());			while ($Database = mysql_fetch_assoc($DatabaseResult))			{			$service_id = $Database['id'];				$name = $Database['name'];			$alternate_name = $Database['alternate_name'];					$S = array();			$S['id'] = $service_id;			$S['name'] = $name;			$S['alternate_name'] = $alternate_name;						array_push($F['services'],$F);					}				$ReturnObject = $F;		}	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>