<?php$route = '/locations/:location_id/languages/:language_id/';$app->get($route, function ($location_id,$language_id)  use ($app){	$ReturnObject = array();	$location_id = mysql_real_escape_string($location_id);	$language_id = mysql_real_escape_string($language_id);	$Query = "SELECT * FROM language WHERE id = '" . $language_id . "' AND location_id = '" . $location_id . "'";	//echo $Query;	$DatabaseResult = mysql_query($Query) or die('Query failed: ' . mysql_error());	while ($Database = mysql_fetch_assoc($DatabaseResult))		{		$language = $Database['language'];		$F = array();		$F['location_id'] = $location_id;		$F['id'] = $language_id;		$F['language'] = $language;				$ReturnObject = $F;		}	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>