<?php$route = '/locations/:location_id/languages/:language_id/';$app->put($route, function ($location_id,$language_id)  use ($app){	$request = $app->request();	$_GET = $request->params();		$location_id = mysql_real_escape_string($location_id);	$language_id = mysql_real_escape_string($language_id);	$language = mysql_real_escape_string($_GET['language']);	$query = "UPDATE language SET ";	$query .= "location_id='" . mysql_real_escape_string($location_id) . "'";	$query .= ",id='" . mysql_real_escape_string($language_id) . "'";	$query .= ",language='" . mysql_real_escape_string($language) . "'";	$query .= " WHERE id = '" . $language_id . "'";	//echo $query;	mysql_query($query) or die('Query failed: ' . mysql_error());	$F = array();	$F['location_id'] = $location_id;	$F['id'] = $language_id;	$F['language'] = $language;		$ReturnObject = $F;	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>