<?php$route = '/locations/:location_id/phones/';$app->get($route, function ($location_id)  use ($app){	$request = $app->request();	$_GET = $request->params();		$location_id = strip_tags(mysql_real_escape_string($location_id));	$ReturnObject = array();	$Query = "SELECT * FROM phone WHERE location_id = '" . $location_id . "'";	$Query .= " ORDER BY number ASC";	//echo $Query;	$DatabaseResult = mysql_query($Query) or die('Query failed: ' . mysql_error());	while ($Database = mysql_fetch_assoc($DatabaseResult))		{		$phone_id = $Database['id'];		$number = $Database['number'];		$extension = $Database['extension'];		$type = $Database['type'];		$department = $Database['department'];		$F = array();		$F['location_id'] = $location_id;		$F['id'] = $phone_id;		$F['number'] = $number;		$F['extension'] = $extension;		$F['type'] = $type;		$F['department'] = $department;				array_push($ReturnObject,$F);				}	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));		});?>