<?php$route = '/locations/:location_id/phones/';$app->post($route, function ($location_id)  use ($app){	$request = $app->request();	$_GET = $request->params();		$clean = filter_input_array(INPUT_POST,$_GET);	$location_id = mysql_real_escape_string($location_id);		if(isset($clean['number']))		{		$phone_id = getGUID();				$number = $clean['number'];		$extension = $clean['extension'];		$type = $clean['type'];		$department = $clean['department'];		$query = "INSERT INTO phone(";				$query .= "location_id,";		$query .= "id,";		$query .= "number,";		$query .= "extension,";		$query .= "type,";		$query .= "department";		$query .= ") VALUES(";		$query .= "'" . mysql_real_escape_string($location_id) . "',";		$query .= "'" . mysql_real_escape_string($phone_id) . "',";		$query .= "'" . mysql_real_escape_string($number) . "',";		$query .= "'" . mysql_real_escape_string($extension) . "',";		$query .= "'" . mysql_real_escape_string($type) . "',";		$query .= "'" . mysql_real_escape_string($department) . "'";		$query .= ")";		mysql_query($query) or die('Query failed: ' . mysql_error());		$F = array();		$F['location_id'] = $location_id;		$F['id'] = $phone_id;		$F['number'] = $number;		$F['extension'] = $extension;		$F['type'] = $type;		$F['department'] = $department;		$ReturnObject = $F;		}			$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>