<?php$route = '/locations/:location_id/';$app->put($route, function ($location_id)  use ($app){	$request = $app->request();	$_GET = $request->params();	$id = filter_var($location_id, FILTER_SANITIZE_STRING);		$name = filter_var($_GET['name'], FILTER_SANITIZE_STRING);	$alternate_name = filter_var($_GET['alternate_name'], FILTER_SANITIZE_STRING);	$transportation = filter_var($_GET['transportation'], FILTER_SANITIZE_STRING);	$latitude = filter_var($_GET['latitude'], FILTER_SANITIZE_STRING);	$longitude = filter_var($_GET['longitude'], FILTER_SANITIZE_STRING);	$Query = "SELECT * FROM location WHERE id = '" . $id . "'";	//echo $Query;	$Database = mysql_query($Query) or die('Query failed: ' . mysql_error());	if($Database && mysql_num_rows($Database))		{		$query = "UPDATE location SET ";				$query .= "name='" . mysql_real_escape_string($name) . "'";		$query .= ",alternate_name='" . mysql_real_escape_string($alternate_name) . "'";		$query .= ",transportation='" . mysql_real_escape_string($transportation) . "'";		$query .= ",latitude='" . mysql_real_escape_string($latitude) . "'";		$query .= ",longitude='" . mysql_real_escape_string($longitude) . "'";		$query .= " WHERE id = '" . $id . "'";		echo $query;		mysql_query($query) or die('Query failed: ' . mysql_error());		}	$F = array();	$F['id'] = $id;	$F['name'] = $name;	$F['alternate_name'] = $alternate_name;	$F['transportation'] = $transportation;	$F['latitude'] = $latitude;	$F['longitude'] = $longitude;		$ReturnObject = $F;	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>