<?php$route = '/locations/:location_id/regular_schedule/';$app->get($route, function ($location_id)  use ($app){	$request = $app->request();	$_GET = $request->params();		$location_id = strip_tags(mysql_real_escape_string($location_id));	$ReturnObject = array();	$Query = "SELECT * FROM regular_schedule WHERE location_id = '" . $location_id . "'";	$Query .= " ORDER BY weekday ASC";	//echo $Query;	$DatabaseResult = mysql_query($Query) or die('Query failed: ' . mysql_error());	while ($Database = mysql_fetch_assoc($DatabaseResult))		{		$regular_schedule_id = $Database['id'];		$weekday = $Database['weekday'];		$opens_at = $Database['opens_at'];		$closes_at = $Database['closes_at'];		$F = array();		$F['location_id'] = $location_id;		$F['id'] = $regular_schedule_id;		$F['weekday'] = $weekday;		$F['opens_at'] = $opens_at;		$F['closes_at'] = $closes_at;				array_push($ReturnObject,$F);				}	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));		});?>