<?php$route = '/locations/:location_id/regular_schedule/:regular_schedule_id/';$app->put($route, function ($location_id,$regular_schedule_id)  use ($app){	$request = $app->request();	$_GET = $request->params();		$location_id = mysql_real_escape_string($location_id);	$regular_schedule_id = mysql_real_escape_string($regular_schedule_id);		$weekday = mysql_real_escape_string($_GET['weekday']);	$opens_at = mysql_real_escape_string($_GET['opens_at']);	$closes_at = mysql_real_escape_string($_GET['closes_at']);	$query = "UPDATE regular_schedule SET ";	$query .= "location_id='" . mysql_real_escape_string($location_id) . "'";	$query .= ",weekday='" . mysql_real_escape_string($weekday) . "'";	$query .= ",opens_at='" . mysql_real_escape_string($opens_at) . "'";	$query .= ",closes_at='" . mysql_real_escape_string($closes_at) . "'";	$query .= " WHERE id = '" . $regular_schedule_id . "'";	//echo $query;	mysql_query($query) or die('Query failed: ' . mysql_error());	$F = array();	$F['location_id'] = $location_id;	$F['id'] = $regular_schedule_id;	$F['weekday'] = $weekday;	$F['opens_at'] = $opens_at;	$F['closes_at'] = $closes_at;	$ReturnObject = $F;	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>