<?php$route = '/locations/:location_id/services/';$app->get($route, function ($location_id)  use ($app){	$request = $app->request();	$_GET = $request->params();		$location_id = strip_tags(mysql_real_escape_string($location_id));	$ReturnObject = array();	$Query = "SELECT * FROM service WHERE location_id = '" . $location_id . "'";	$Query .= " ORDER BY name ASC";	//echo $Query;	$DatabaseResult = mysql_query($Query) or die('Query failed: ' . mysql_error());	while ($Database = mysql_fetch_assoc($DatabaseResult))		{		$service_id = $Database['id'];			$name = $Database['name'];		$alternate_name = $Database['alternate_name'];			$F = array();		$F['location_id'] = $location_id;		$F['id'] = $service_id;		$F['name'] = $name;		$F['alternate_name'] = $alternate_name;				array_push($ReturnObject,$F);				}	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));		});?>