<?php$route = '/locations/:location_id/services/:service_id/';$app->put($route, function ($location_id,$service_id)  use ($app){	$request = $app->request();	$_GET = $request->params();		$location_id = mysql_real_escape_string($location_id);	$service_id = mysql_real_escape_string($service_id);	$name = $_GET['name'];	$alternate_name = $_GET['alternate_name'];		$query = "UPDATE service SET ";	$query .= "location_id='" . mysql_real_escape_string($location_id) . "'";	$query .= ",name='" . mysql_real_escape_string($name) . "'";	$query .= ",alternate_name='" . mysql_real_escape_string($alternate_name) . "'";	$query .= " WHERE id = '" . $service_id . "'";	//echo $query;	mysql_query($query) or die('Query failed: ' . mysql_error());	$F = array();	$F['location_id'] = $location_id;	$F['id'] = $service_id;	$F['name'] = $name;	$F['alternate_name'] = $alternate_name;		$ReturnObject = $F;	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>