<?php$route = '/locations/';$app->post($route, function ()  use ($app){ 	$ReturnObject = array(); 	$request = $app->request();	$_GET = $request->params();	$clean = filter_input_array(INPUT_POST,$_GET);	if(isset($clean['name']))		{		$id = getGUID();		if(isset($clean['name'])){ $name = $clean['name']; } else { $name = '';}		if(isset($clean['alternate_name'])){ $alternate_name = $clean['alternate_name']; } else { $alternate_name = '';}		if(isset($clean['transportation'])){ $transportation = $clean['transportation']; } else { $transportation = '';}		if(isset($clean['latitude'])){ $latitude = $clean['latitude']; } else { $latitude = '';}		if(isset($clean['longitude'])){ $longitude = $clean['longitude']; } else { $longitude = '';}			$query = "INSERT INTO location(";			$query .= "id,";		$query .= "name,";		$query .= "alternate_name,";		$query .= "transportation,";		$query .= "latitude,";		$query .= "longitude";			$query .= ") VALUES(";			$query .= "'" . mysql_real_escape_string($id) . "',";		$query .= "'" . mysql_real_escape_string($name) . "',";		$query .= "'" . mysql_real_escape_string($alternate_name) . "',";		$query .= "'" . mysql_real_escape_string($transportation) . "',";		$query .= "'" . mysql_real_escape_string($latitude) . "',";		$query .= "'" . mysql_real_escape_string($longitude) . "'";			$query .= ")";		//echo $query;		mysql_query($query) or die('Query failed: ' . mysql_error());			$F = array();		$F['id'] = $id;		$F['name'] = $name;		$F['alternate_name'] = $alternate_name;		$F['transportation'] = $transportation;		$F['latitude'] = $latitude;		$F['longitude'] = $longitude;				$ReturnObject = $F;		}	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>