<?php$route = '/metadata';$app->get($route, function ()  use ($app){	$request = $app->request();	$_GET = $request->params();	$ReturnObject = array();	if($query=='')		{		$Query = "SELECT * FROM metadata WHERE name LIKE '%" . $query . "%'";		}	else		{		$Query = "SELECT * FROM metadata";		}	$Query .= " ORDER BY name ASC";	$DatabaseResult = mysql_query($Query) or die('Query failed: ' . mysql_error());	while ($Database = mysql_fetch_assoc($DatabaseResult))		{		$id = $Database['id'];		$resource_id = $Database['resource_id'];		$last_action_date = $Database['last_action_date'];		$last_action_type = $Database['last_action_type'];		$field_name = $Database['field_name'];		$previous_value = $Database['previous_value'];		$replacement_value = $Database['replacement_value'];		$updated_by = $Database['updated_by'];		$F = array();		$F['id'] = $id;		$F['resource_id'] = $resource_id;		$F['last_action_date'] = $last_action_date;		$F['last_action_type'] = $last_action_type;		$F['field_name'] = $field_name;		$F['previous_value'] = $previous_value;		$F['replacement_value'] = $replacement_value;		$F['updated_by'] = $updated_by;		array_push($ReturnObject, $F);		}	$api->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>