<?php$route = '/organizations/';$app->get($route, function ()  use ($app){	$request = $app->request();	$_GET = $request->params();	if(isset($_GET['query'])){ $query = $_GET['query']; } else { $query = '';}	$ReturnObject = array();	if($query=='')		{		$Query = "SELECT * FROM organization WHERE name LIKE '%" . $query . "%'";		}	else		{		$Query = "SELECT * FROM organization";		}	$query .= " ORDER BY name ASC";	$DatabaseResult = mysql_query($Query) or die('Query failed: ' . mysql_error());	while ($Database = mysql_fetch_assoc($DatabaseResult))		{		$id = $Database['id'];		$name = $Database['name'];		$alternate_name = $Database['alternate_name'];		$description = $Database['description'];		$email = $Database['email'];		$url = $Database['url'];		$tax_status = $Database['tax_status'];		$tax_id = $Database['tax_id'];		$year_incorporated = $Database['year_incorporated'];		$legal_status = $Database['legal_status'];		$F = array();		$F['id'] = $id;		$F['name'] = $name;		$F['alternate_name'] = $alternate_name;		$F['description'] = $description;		$F['email'] = $email;		$F['url'] = $url;		$F['tax_status'] = $tax_status;		$F['tax_id'] = $tax_id;		$F['year_incorporated'] = $year_incorporated;		$F['legal_status'] = $legal_status;		array_push($ReturnObject, $F);		}	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>