<?php$route = '/organizations/:organization_id/funding/:funding_id/';$app->get($route, function ($organization_id,$funding_id)  use ($app){	$ReturnObject = array();	$organization_id = mysql_real_escape_string($organization_id);	$funding_id = mysql_real_escape_string($funding_id);	$Query = "SELECT * FROM funding WHERE id = '" . $funding_id . "' AND organization_id = '" . $organization_id . "'";	//echo $Query;	$DatabaseResult = mysql_query($Query) or die('Query failed: ' . mysql_error());	while ($Database = mysql_fetch_assoc($DatabaseResult))		{		$source = $Database['source'];		$F = array();			$F['id'] = $funding_id;		$F['organization_id'] = $organization_id;		$F['source'] = $source;						$ReturnObject = $F;		}	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>