<?php$route = '/organizations/:organization_id/funding/';$app->get($route, function ($organization_id)  use ($app){	$request = $app->request();	$_GET = $request->params();		$organization_id = strip_tags(mysql_real_escape_string($organization_id));	$ReturnObject = array();	$Query = "SELECT * FROM funding WHERE organization_id = '" . $organization_id . "'";	$Query .= " ORDER BY source ASC";	//echo $Query;	$DatabaseResult = mysql_query($Query) or die('Query failed: ' . mysql_error());	while ($Database = mysql_fetch_assoc($DatabaseResult))		{		$funding_id = $Database['id'];		$source = $Database['source'];		$F = array();			$F['id'] = $funding_id;		$F['organization_id'] = $organization_id;		$F['source'] = $source;					array_push($ReturnObject,$F);				}	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));		});?>