<?php$route = '/organizations/:organization_id/locations/:location_id/';$app->get($route, function ($organization_id,$location_id)  use ($app){	$ReturnObject = array();	$organization_id = mysql_real_escape_string($organization_id);	$location_id = mysql_real_escape_string($location_id);	$Query = "SELECT * FROM location WHERE id = '" . $location_id . "' AND organization_id = '" . $organization_id . "'";	//echo $Query;	$DatabaseResult = mysql_query($Query) or die('Query failed: ' . mysql_error());	while ($Database = mysql_fetch_assoc($DatabaseResult))		{		$name = $Database['name'];		$alternate_name = $Database['alternate_name'];		$transportation = $Database['transportation'];		$latitude = $Database['latitude'];		$longitude = $Database['longitude'];		$F = array();		$F['organization_id'] = $organization_id;		$F['id'] = $location_id;		$F['name'] = $name;		$F['alternate_name'] = $alternate_name;		$F['transportation'] = $transportation;		$F['latitude'] = $latitude;		$F['longitude'] = $longitude;				$ReturnObject = $F;		}	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>