<?php$route = '/organizations/:organization_id/locations/:location_id/';$app->put($route, function ($organization_id,$location_id)  use ($app){	$request = $app->request();	$_GET = $request->params();		$organization_id = mysql_real_escape_string($organization_id);	$id = mysql_real_escape_string($location_id);		$name = mysql_real_escape_string($_GET['name']);	$alternate_name = mysql_real_escape_string($_GET['alternate_name']);	$transportation = mysql_real_escape_string($_GET['transportation']);	$latitude = mysql_real_escape_string($_GET['latitude']);	$longitude = mysql_real_escape_string($_GET['longitude']);	$query = "UPDATE location SET ";	$query .= "organization_id='" . mysql_real_escape_string($organization_id) . "'";	$query .= ",id='" . mysql_real_escape_string($location_id) . "'";	$query .= ",name='" . mysql_real_escape_string($name) . "'";	$query .= ",alternate_name='" . mysql_real_escape_string($alternate_name) . "'";	$query .= ",transportation='" . mysql_real_escape_string($transportation) . "'";	$query .= ",latitude='" . mysql_real_escape_string($latitude) . "'";	$query .= ",longitude='" . mysql_real_escape_string($longitude) . "'";	$query .= " WHERE id = '" . $location_id . "'";	//echo $query;	mysql_query($query) or die('Query failed: ' . mysql_error());	$F = array();	$F['organization_id'] = $organization_id;	$F['id'] = $id;	$F['name'] = $name;	$F['alternate_name'] = $alternate_name;	$F['transportation'] = $transportation;	$F['latitude'] = $latitude;	$F['longitude'] = $longitude;		$ReturnObject = $F;	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>