<?php$route = '/organizations/:organization_id/phones/:phone_id/';$app->get($route, function ($organization_id,$phone_id)  use ($app){	$ReturnObject = array();	$organization_id = mysql_real_escape_string($organization_id);	$phone_id = mysql_real_escape_string($phone_id);	$Query = "SELECT * FROM phone WHERE id = '" . $phone_id . "' AND organization_id = '" . $organization_id . "'";	//echo $Query;	$DatabaseResult = mysql_query($Query) or die('Query failed: ' . mysql_error());	while ($Database = mysql_fetch_assoc($DatabaseResult))		{		$number = $Database['number'];		$extension = $Database['extension'];		$type = $Database['type'];		$department = $Database['department'];		$F = array();		$F['organization_id'] = $organization_id;		$F['id'] = $phone_id;		$F['number'] = $number;		$F['extension'] = $extension;		$F['type'] = $type;		$F['department'] = $department;				$ReturnObject = $F;		}	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>