<?php$route = '/organizations/:organization_id/programs/:program_id/services/';$app->post($route, function ()  use ($app){	$request = $app->request();	$_GET = $request->params();	if(isset($_GET['id'])){ $id = $_GET['id']; } else { $id = '';}	if(isset($_GET['name'])){ $name = $_GET['name']; } else { $name = '';}	if(isset($_GET['alternate_name'])){ $alternate_name = $_GET['alternate_name']; } else { $alternate_name = '';}	$slug = PrepareFileName($name);	$Query = "SELECT * FROM service WHERE name = '" . $name . "'";	$Database = mysql_query($Query) or die('Query failed: ' . mysql_error());	if($Database && mysql_num_rows($Database))		{		$Link = mysql_fetch_assoc($Database);		$ReturnObject = array();		$ReturnObject['message'] = "Service Already Exists!";		$ReturnObject['slug'] = $slug;		}	else		{		$query = "INSERT INTO service(";		if(isset($organization_id)){ $query .= $organization_id . ","; }		if(isset($id)){ $query .= $id . ","; }		if(isset($name)){ $query .= $name . ","; }		if(isset($alternate_name)){ $query .= $alternate_name . ","; }		$query .= ") VALUES(";		if(isset($organization_id)){ $query .= "'" . mysql_real_escape_string($organization_id) . "',"; }		if(isset($id)){ $query .= "'" . mysql_real_escape_string($id) . "',"; }		if(isset($name)){ $query .= "'" . mysql_real_escape_string($name) . "',"; }		if(isset($alternate_name)){ $query .= "'" . mysql_real_escape_string($alternate_name) . "',"; }		$query .= ")";		mysql_query($query) or die('Query failed: ' . mysql_error());		$ReturnObject = array();		$ReturnObject['message'] = "Service Added!";		$ReturnObject['slug'] = $slug;		}	$api->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>