<?php$route = '/organizations/:organization_id/programs/:program_id/services/:service_id';$app->put($route, function ()  use ($app){	$request = $app->request();	$_GET = $request->params();	if(isset($_GET['name'])){ $name = $_GET['name']; } else { $name = '';}	if(isset($_GET['alternate_name'])){ $alternate_name = $_GET['alternate_name']; } else { $alternate_name = '';}	$Query = "SELECT * FROM service WHERE slug = '" . $slug . "'";	$Database = mysql_query($Query) or die('Query failed: ' . mysql_error());	if($Database && mysql_num_rows($Database))		{		$query = "UPDATE service SET";		if(isset($organization_id))			{			$query .= "organization_id='" . mysql_real_escape_string($organization_id) . "'";			}		if(isset($program_id))			{			$query .= "program_id='" . mysql_real_escape_string($program_id) . "'";			}		if(isset($name))			{			$query .= "name='" . mysql_real_escape_string($name) . "'";			}		if(isset($alternate_name))			{			$query .= "alternate_name='" . mysql_real_escape_string($alternate_name) . "'";			}		$query .= " WHERE slug = '" . $slug . "'";		mysql_query($query) or die('Query failed: ' . mysql_error());		$ReturnObject = array();		$ReturnObject['message'] = "Service Updated!";		$ReturnObject['slug'] = $slug;		}	$api->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>