<?php$route = '/organizations/:organization_id/services/:service_id/';$app->get($route, function ($organization_id,$service_id)  use ($app){	$ReturnObject = array();	$organization_id = mysql_real_escape_string($organization_id);	$service_id = mysql_real_escape_string($service_id);	$Query = "SELECT * FROM service WHERE id = '" . $service_id . "' AND organization_id = '" . $organization_id . "'";	//echo $Query;	$DatabaseResult = mysql_query($Query) or die('Query failed: ' . mysql_error());	while ($Database = mysql_fetch_assoc($DatabaseResult))		{		$name = $Database['name'];		$alternate_name = $Database['alternate_name'];			$F = array();		$F['organization_id'] = $organization_id;		$F['id'] = $service_id;		$F['name'] = $name;		$F['alternate_name'] = $alternate_name;				$ReturnObject = $F;		}	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>