<?php$route = '/services/:service_id/eligibility/';$app->get($route, function ($service_id)  use ($app){	$request = $app->request();	$_GET = $request->params();		$service_id = strip_tags(mysql_real_escape_string($service_id));	$ReturnObject = array();	$Query = "SELECT * FROM eligibility WHERE service_id = '" . $service_id . "'";	$Query .= " ORDER BY source ASC";	//echo $Query;	$DatabaseResult = mysql_query($Query) or die('Query failed: ' . mysql_error());	while ($Database = mysql_fetch_assoc($DatabaseResult))		{		$eligibility_id = $Database['id'];		$source = $Database['source'];		$F = array();			$F['id'] = $eligibility_id;		$F['service_id'] = $service_id;		$F['source'] = $source;					array_push($ReturnObject,$F);				}	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));		});?>